color: #BBBBBB;\n}\n.postbody ul.profile-icons li a:hover {\n	color: #111;\n}\n\nli.warn-icon a:hover {\n	color: #C44664!important;\n}\n.ic-reported,.ic-unaproved {   \n  color: #C44664!important;\n}\n \n.icon-picture {   \n  color: #888888!important;\n}\n/* Content container styles\n----------------------------------------*/\n.content {\n	color: #AAAAAA;\n}\n\n.content h2, .panel h2 {\n	color: #AAAAAA;\n	border-bottom: 4px solid #444444;\n}\n\ndl.faq dt {\n	color: #F7424E;\n}\n\n.posthilit {\n	background-color: #F3BFCC;\n	color: #BC2A4D;\n}\n\n/* Post signature */\n.signature {\n	border-top-color: #666666;\n}\n\n/* Post noticies */\n.notice {\n	border-top-color:  #666666;\n}\n\n/* BB Code styles\n----------------------------------------*/\n/* Quote block */\nblockquote {\n	background-color: #383838;\n	background-image: url("styles/Flato - Dark Skin-Red/theme/images/quote.png");\n	border-color:#222222;\n}\n\n.rtl blockquote {\n	background-image: url("styles/Flato - Dark Skin-Red/theme/images/quote_rtl.png");\n}\n\nblockquote blockquote {\n	/* Nested quotes */\n	background-color:#333333;\n}\n\nblockquote blockquote blockquote {\n	/* Nested quotes */\n	background-color: #383838;\n}\n\n/* Code block */\ndl.codebox {\n	background-color: #383838;\n	border-color: #222222;\n}\n\ndl.codebox dt {\n	border-bottom-color:  #222222;\n}\n\ndl.codebox code {\n	color: #2E8B57;\n}\n\n.syntaxbg		{ color: #FFFFFF; }\n.syntaxcomment	{ color: #FF8000; }\n.syntaxdefault	{ color: #AAAAAA; }\n.syntaxhtml		{ color: #AAAAAA; }\n.syntaxkeyword	{ color: #007700; }\n.syntaxstring	{ color: #DD0000; }\n\n/* Attachments\n----------------------------------------*/\n.attachbox {\n	background-color: #383838;\n	border-color:  #222222;\n}\n\n.pm-message .attachbox {\n	background-color: #383838;\n}\n\n.attachbox dd {\n	border-top-color: #C9D2D8;\n}\n\n.attachbox p {\n	color: #666666;\n}\n\n.attachbox p.stats {\n	color: #666666;\n}\n\n.attach-image img {\n	border-color: #999999;\n}\n\n/* Inline image thu