lock;\n}\n\ndl.file dt {\n	text-transform: none;\n	margin: 0;\n	padding: 0;\n	font-weight: bold;\n	font-family: Verdana, Arial, Helvetica, sans-serif;\n}\n\ndl.file dd {\n	color: #666666;\n	margin: 0;\n	padding: 0;	\n}\n\ndl.thumbnail img {\n	padding: 3px;\n	border: 1px solid #666666;\n	background-color: #FFF;\n}\n\ndl.thumbnail dd {\n	color: #666666;\n	font-style: italic;\n	font-family: Verdana, Arial, Helvetica, sans-serif;\n}\n\n.attachbox dl.thumbnail dd {\n	font-size: 100%;\n}\n\ndl.thumbnail dt a:hover {\n	background-color: #EEEEEE;\n}\n\ndl.thumbnail dt a:hover img {\n	border: 1px solid #d2d2d2;\n}\n\n/* Post poll styles\n----------------------------------------*/\n\n\nfieldset.polls dl {\n	margin-top: 5px;\n	border-top: 1px solid #e2e2e2;\n	padding: 5px 0 0 0;\n	line-height: 120%;\n	color: #666666;\n}\n\nfieldset.polls dl.voted {\n	font-weight: bold;\n	color: #000000;\n}\n\nfieldset.polls dt {\n	text-align: left;\n	float: left;\n	display: block;\n	width: 30%;\n	border-right: none;\n	padding: 0;\n	margin: 0;	\n}\n\nfieldset.polls dd {\n	float: left;\n	width: 10%;\n	border-left: none;\n	padding: 0 5px;\n	margin-left: 0;	\n}\n\nfieldset.polls dd.resultbar {\n	width: 50%;\n}\n\nfieldset.polls dd input {\n	margin: 2px 0;\n}\n\nfieldset.polls dd div {\n	text-align: right;\n	font-family: Arial, Helvetica, sans-serif;\n	color: #FFFFFF;\n	font-weight: bold;\n	padding: 0 2px;\n	overflow: visible;\n	min-width: 2%;\n}\n\n.pollbar1 {\n	background-color: #aaaaaa;\n	border-bottom: 1px solid #747474;\n	border-right: 1px solid #747474;\n}\n\n.pollbar2 {\n	background-color: #bebebe;\n	border-bottom: 1px solid #8c8c8c;\n	border-right: 1px solid #8c8c8c;\n}\n\n.pollbar3 {\n	background-color: #D1D1D1;\n	border-bottom: 1px solid #aaaaaa;\n	border-right: 1px solid #aaaaaa;\n}\n\n.pollbar4 {\n	background-color: #e4e4e4;\n	border-bottom: 1px solid #bebebe;\n	border-right: 1px solid #bebebe;\n}\n\n.pollbar5 {\n	background-color: #f8f8f8;\n	border-bottom: 1px solid #D1D1D1;\n	border-right: 1px solid #D1D1D1;\n}\n\n/* Poster profile block\n----------------------------------------*/\n.postprofile {\n	/* Also see tweaks.css */	\n	min-height: 80px;	\n	width: 20%;\n	float: left;\n	display: table;	\n}\n\n.postprofile dd, .postprofile dt {\n	line-height: 1.2em;	\n}\n.postprofile dd {   \n   padding: 4px 0 4px 5px;\n   margin-top: 2px;    \n   display: block;   \n   text-transform: lowercase;\n   position: relative;   \n}\n.postprofile strong {\n	font-weight: normal;	\n    font-family: ''Oswald'', sans-serif;;\n    font-size: 12px;    \n    text-transform: lowercase;       \n    width: auto;\n    float: left;\n    width: 50px;\n    overflow: hidden;    \n}\n.search .postprofile strong {\n	float: none;\n}	\n.postprofile p {\n	padding-left: 5px;	\n	overflow: hidden; \n}\nul.profile-icons li {\n  float: left;\n  font-size: 16px;\n  margin: 0 10px 2px 0;\n}  \n\n.postprofile dt a img {   \n   padding: 5px;    \n}\n.postprofile dt {    \n   padding-bottom: 5px;   \n   text-align: center;\n}\n.postprofile .aim-icon i {   \n   font-size: 14px;\n   padding-left: 3px;   \n}\n.avatar {\n  background: none repeat scroll 0 0 #FAFAFA;\n  border: 1px solid #CCCCCC;\n  padding: 5px;\n}\n\n.status-position {\n	float: left;\n	text-align: left;\n}	\n\n.online,.offline {	\n	display: block;	\n	padding: 2px;\n	margin-left: 2px;\n	height: 16px;	\n	font-size: 12px;\n	width: 100%;\n}\n\n/* Poster profile used by search*/\n.search .postprofile {\n	width: 30%;\n}\n\n/* pm list in compose message if mass pm is enabled */\ndl.pmlist dt {\n	width: 60% !important;\n}\n\ndl.pmlist dt textarea {\n	width: 95%;\n}\n\ndl.pmlist dd {\n	margin-left: 61% !important;\n	margin-bottom: 2px;\n}\n/* Button Styles\n---------------------------------------- */\n\nbutton::-moz-focus-inner,\ninput[type="button"]::-moz-focus-inner,\ninput[type="submit"]::-moz-focus-inner,\ninput[type="reset"]::-moz-focus-inner {\n     padding: 0 !important;\n     border: 0 none !important;\n }\n.buttons i {    \n  font-size: 1.3em!important;  \n  position: relative;    \n  top: 1px;\n  padding-right: 8px;\n}\n.buttons .icon-left-open-mini {\n  margin-left: -5px!important;\n}\n.buttons .icon-right-open-mini {\n  margin-right: -5px!important;\n  padding-left: 5px!important;\n}\n.buttons .icon-reply {\n  margin-left: -2px!important;\n\n}\n.buttons {\n  float: left;\n  margin-bottom: 5px;\n  margin-top: 5px;   \n}\n.buttons a { \n  line-height: 17px;\n  padding: 8px 10px;  \n  position: relative;\n  font-size: 13px;\n  overflow: hidden;  \n  display: block;\n  float: left;\n  margin-right: 5px;\n\n}\n#cp-main .buttons {\n	background: none;\n	border: none;\n\n}\n#cp-main .buttons div {\n	float: left;	\n}\n\n/* Poster profile icons\n----------------------------------------*/\nul.profile-icons {\n	padding-top: 5px;\n	list-style: none;\n}\n\n/* Rolloff state */\nul.profile-icons li a {\n	display: block;\n	width: 100%;\n	height: 100%;	\n}\n\n/* Hide <a> text and hide off-state image when rolling over (prevents flicker in IE) */\nul.profile-icons li span { display:none; }\n\n/* Positioning of moderator icons */\n.postbody ul.profile-icons {\n	float: right;\n	width: auto;\n	padding: 0;\n}\n.postbody ul.profile-icons li {\n	margin: 0 11px 3px 0;\n	float: left;\n	font-size: 1.462em;\n}\n.postbody .report-icon i,.postbody .info-icon i,.postbody .edit-icon i{	\n	font-size: 1.1em;\n}\n.go-top {\n	position: fixed;\n	bottom: 2em;\n	right: 2em;\n	text-decoration: none;	\n	font-size: 16px;\n	padding: 9px 12px 10px 12px;\n	display: none;\n	z-index: 99999;\n}\n/* Control Panel Styles\n---------------------------------------- */\n\n\n/* Main CP box\n----------------------------------------*/\n#cp-menu {\n	float:left;\n	width: 19%;\n	margin-top: 20px;\n	margin-bottom: 5px;\n}\n\n#cp-main {\n	float: left;\n	margin-left: 20px;\n	width: 78%;\n	margin-top: 20px;\n}\n#cp-main .postbody {\n	float: left!important;\n}\n#cp-main .postprofile {\n	float: right!important;\n}\n\n#cp-main .content {\n	padding: 0;\n}\n\n#cp-main .panel p {\n	font-size: 1.1em;\n}\n\n#cp-main .panel ol {\n	margin-left: 2em;\n	font-size: 1.1em;\n}\n\nul.cplist {\n	margin-bottom: 5px;\n	border-top: 1px solid #cbcbcb;\n}\n\n#cp-main table.table1 {\n	margin-bottom: 1em;\n}\n\n#cp-main table.table1 tbody th {\n	font-style: italic;\n	background-color: transparent !important;\n	border-bottom: none;\n}\n\n#cp-main .pagination {\n	float: right;\n	width: auto;\n	padding-top: 1px;\n}\n\n#cp-main .postbody p {\n	font-size: 1.1em;\n}\n\n#cp-main .pm-message {\n	border: 1px solid #e2e2e2;\n	margin: 10px 0;\n	background-color: #FFFFFF;\n	width: auto;\n	float: none;\n}\n\n.pm-message h2 {\n	padding-bottom: 5px;\n}\n\n#cp-main .postbody h3, #cp-main .box2 h3 {\n	margin-top: 0;\n}\n\n#cp-main .buttons {\n	margin-left: 0;\n}\n\n#cp-main ul.linklist {\n	margin: 0;\n	width: 100%;\n}\n#postingbox .fields1 dd .button2 {\n	padding: 0 6px 2px!important;\n}\n/* MCP Specific tweaks */\n.mcp-main .postbody {\n	width: 100%;\n}\n\n.tabs-container h2 {\n	float: left;\n	margin-bottom: 0px;\n}\n\n.tabs-container #minitabs {\n	float: right;\n	margin-top: 19px;\n}\n\n.tabs-container:after {\n	display: block;\n	clear: both;\n	content: '''';\n}\n\n/* CP tabbed menu\n----------------------------------------*/\n#cp-main .panel {     \n    padding: 0!important;	\n}\n#cp-main .panel .header dt{    \n    padding-left: 10px;   \n}\n#tabs {\n	line-height: normal;\n	margin: 20px 0 -1px 7px;\n	min-width: 570px;\n}\n\n#tabs ul {\n	margin:0;\n	padding: 0;\n	list-style: none;\n}\n\n#tabs li {\n	display: inline;\n	margin: 0;\n	padding: 0;\n	font-size: 1em;\n	font-weight: bold;\n}\n\n#tabs a {\n	float: left;	\n    margin: 5px 5px 0 0;\n    padding: 0;    \n	text-decoration: none;\n	position: relative;\n	cursor: pointer;\n}\n\n#tabs a span {\n	float: left;\n	display: block;	\n	padding: 8px 10px;\n	background-color: #FAFAFA;\n    border: 1px solid #CCCCCC;    \n	white-space: nowrap;\n}\n\n#tabs a:hover span {\n	color: #bcbcbc;\n}\n\n#tabs .activetab a {\n	background-position: 0 0;\n	border-bottom: 1px solid #ebebeb;\n}\n\n#tabs .activetab a span {\n	background-position: 100% 0;\n	padding-bottom: 7px;\n	color: #333333;\n}\n\n#tabs a:hover {\n	background-position: 0 -70px;\n}\n\n#tabs a:hover span {\n	background-position:100% -70px;\n}\n\n#tabs .activetab a:hover {\n	background-position: 0 0;\n}\n\n#tabs .activetab a:hover span {\n	color: #000000;\n	background-position: 100% 0;\n}\n\n/* Mini tabbed menu used in MCP\n----------------------------------------*/\n#minitabs {\n	line-height: normal;\n	margin: -20px 7px 0 0;\n}\n\n#minitabs ul {\n	margin:0;\n	padding: 0;\n	list-style: none;\n}\n\n#minitabs li {\n	display: block;\n	float: right;\n	padding: 10px 5px;\n	font-size: 1em;\n	font-weight: bold;\n	background-color: #f2f2f2;\n	margin-left: 5px;\n}\n\n#minitabs a {\n}\n\n#minitabs a:hover {\n	text-decoration: none;\n}\n\n#minitabs li.activetab {\n	background-color: #F9F9F9;\n}\n\n#minitabs li.activetab a, #minitabs li.activetab a:hover {\n	color: #333333;\n}\n\n/* UCP navigation menu\n----------------------------------------*/\n/* Container for sub-navigation list */\n#navigation {\n	width: 100%;\n	padding-top: 36px;\n}\n\n#navigation ul {\n	list-style:none;\n}\n\n/* Default list state */\n#navigation li {\n	margin: 1px 0;\n	padding: 0;\n	font-weight: bold;\n	display: inline;\n}\n\n/* Link styles for the sub-section links */\n#navigation a {	\n	padding: 8px 10px;	\n	display: block;	\n	margin: 5px 0;\n	text-decoration: none;\n	font-weight: bold;\n	word-wrap: break-word;\n}\n\n#navigation a:hover {\n	text-decoration: none;		\n}\n\n#navigation #active-subsection a {\n	display: block;	\n}\n\n#navigation #active-subsection a:hover {\n	text-decoration: none;	\n}\n\n/* Preferences pane layout\n----------------------------------------*/\n#cp-main h2 {\n	border-bottom: none;\n	padding: 0;	\n	width: 100%;\n}\n\n/* Friends list */\n.cp-mini {	\n	padding: 0 5px;\n	margin-top: 10px;\n}\n\n.cp-mini span.corners-top, .cp-mini span.corners-bottom {\n	margin: 0 -5px;\n}\n\ndl.mini dt {\n	font-weight: bold;\n	margin-bottom: 5px;\n	\n}\n\ndl.mini dd {\n	padding-top: 4px;\n}\n\n.friend-online {\n	font-weight: bold;\n}\n\n.friend-offline {\n	font-style: italic;\n}\n\n/* PM Styles\n----------------------------------------*/\n#pm-menu {\n	line-height: 2.5em;\n}\n\n/* PM panel adjustments */\n.reply-all a.left {\n	background-position: 3px 60%;\n}\n\n.reply-all a.left:hover {\n	background-position: 0px 60%;\n}\n\n.reply-all {\n	font-size: 11px;\n	padding-top: 5px;\n}\n\n\n/* Defined rules list for PM options */\nol.def-rules {\n	padding-left: 0;\n}\n\nol.def-rules li {\n	line-height: 180%;\n	padding: 1px;\n}\n\n/* PM marking colours */\n\n.pmlist li.pm_message_reported_colour, .pm_message_reported_colour {\n	border-left-color: #bcbcbc;\n	border-right-color: #bcbcbc;\n}\n\n.pmlist li.pm_marked_colour, .pm_marked_colour {	\n	border: solid 3px #ffffff;\n	border-width: 0 3px;\n}\n\n.pmlist li.pm_replied_colour, .pm_replied_colour {	\n	border: solid 3px #c2c2c2;\n	border-width: 0 3px;\n}\n\n.pmlist li.pm_friend_colour, .pm_friend_colour {	\n	border: solid 3px #bdbdbd;\n	border-width: 0 3px;\n}\n\n.pmlist li.pm_foe_colour, .pm_foe_colour {\n	padding: 0;\n	border: solid 3px #000000;\n	border-width: 0 3px;\n}\n\n.pm-legend {\n	border-left-width: 10px;\n	border-left-style: solid;\n	border-right-width: 0;\n	margin-bottom: 3px;\n	padding-left: 3px;\n}\n\n/* Avatar gallery */\n#gallery label {\n	position: relative;\n	float: left;\n	margin: 10px;\n	padding: 5px;\n	width: auto;\n	background: #FFFFFF;\n	border: 1px solid #CCC;\n	text-align: center;\n}\n\n#gallery label:hover {\n	background-color: #EEE;\n}\n/* Form Styles\n---------------------------------------- */\n\n/* General form styles\n----------------------------------------*/\nfieldset {\n	border-width: 0;	\n}\n\ninput {\n	font-weight: normal;	\n	vertical-align: middle;	\n	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;	\n    outline: none;\n}\n\nselect {\n	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;\n	font-weight: normal;\n	cursor: pointer;\n	vertical-align: middle;	    \n    padding: 7px 6px;\n    outline: none;\n    max-width: 240px;\n    	\n}\n\noption {\n	padding-right: 1em;\n}\n\noption.disabled-option {\n	color: graytext;\n}\n\ntextarea {\n	font-family: "Helvetica Neue",Helvetica,Arial,sans-serif;\n	width: 60%;\n	padding: 2px;\n	font-size: 1em;\n	line-height: 1.4em;\n}\n\nlabel {\n	cursor: default;\n	padding-right: 5px;	\n}\n\nlabel input {\n	vertical-align: middle;\n}\n\nlabel img {\n	vertical-align: middle;\n}\n\n/* Definition list layout for forms\n---------------------------------------- */\nfieldset dl {\n	padding: 4px 0;\n}\n\nfieldset dt {\n	float: left;	\n	width: 40%;\n	text-align: left;\n	display: block;\n}\n\nfieldset dd {	\n	vertical-align: top;\n	margin-bottom: 3px;\n}\n\nfieldset dd:before,\nfieldset dd:after {\n  content: "";\n  display: table;\n  line-height: 0;\n}\n\nfieldset dd:after {\n  clear: both;\n}\n\n#colour_palette a {\n	display: block;\n}	\n\n/* Specific layout 1 */\nfieldset.fields1 dt {\n	width: 100%;\n	border-right-width: 0;\n	margin-bottom: 3px;\n}\n\nfieldset.fields1 dd {\n	margin-left: 0;	\n	border-left-width: 0;\n}\n\nfieldset.fields1 {\n	background-color: transparent;\n}\n\nfieldset.fields1 div {\n	margin-bottom: 3px;\n}\n\n/* Set it back to 0px for the reCaptcha divs: PHPBB3-9587 */\nfieldset.fields1 #recaptcha_widget_div div {\n	margin-bottom: 0;\n}\n\n/* Specific layout 2 */\nfieldset.fields2 dt {\n	width: 15em;\n	border-right-width: 0;\n}\n\nfieldset.fields2 dd {\n	margin-left: 16em;\n	border-left-width: 0;\n}\n\n/* Form elements */\ndt label {\n	font-weight: bold;\n	text-align: left;\n}\n\ndd label {	\n	color: #333;\n}\n\ndd input, dd textarea {\n	margin-right: 3px;\n}\n\ndd select {\n	width: auto;\n}\n\ndd textarea {\n	width: 85%;\n}\n\n/* Hover effects */\nfieldset dl:hover dt label {\n	color: #000000;\n}\n\nfieldset.fields2 dl:hover dt label {