 \n  .sidebar-inside-sb input.inputbox {    \n    width: 90%!important;\n  } \n  #page-copyrights {\n    padding-left: 10px;\n    padding-right: 10px;\n  } \n  input.input-login{ \n  width: 100%;   \n  vertical-align: middle;\n  text-indent: 34px;\n  }\n  /* UCP styles */\n  #cp-menu,#cp-main {    \n    width: 100%!important;\n    float: none;\n  } \n  #cp-main {    \n    margin-left: 0!important;    \n  }\n  #tabs {    \n    min-width: 100%!important;  \n  }\n  #postingbox .fields1 dd .button2 {\n    padding:8px 10px!important;\n  }\n  #cp-main dd.moderation {\n    display: none!important;\n  }\n  fieldset.fields1 dd {  \n  margin-left: 0;\n  }\n  .left-box {\n    float: none!important;\n    text-align: left;\n    margin-bottom: 5px;\n    width: auto;\n  }\n  #cp-main dd.info {\n    display: none!important;\n  }  \n}\n\n@media (min-width: 481px) and (max-width: 767px) {\n\n  .go-top,dd.topics,dd.replies,dd.posts,.panel-icon,dd.views {\n    display: none!important;\n  }\n  ul.topiclist dt {\n    width: 55%!important;\n  }\n  .header dt,.panel-header {\n    padding-left: 5px!important;\n  }   \n /* Footer styles */\n  \n  .footer-block {    \n    margin-right: 30px;    \n    width: 100%;\n    margin-top: 10px;\n  }\n  .footer-l {    \n    width: 55%;\n  }\n  .footer-r {    \n    width: 42%;\n  }\n  .about {\n    margin-right: 50px;    \n  }\n  .footer-social {   \n    margin-top: 10px;   \n  }\n  /* Remind Box */\n   \n  #remind input.medium {\n    width: 80%!important;\n  } \n\n  /* Register Page */\n  \n  #register .autowidth {\n   width: 63%!important;\n  } \n  fieldset dt {   \n    width: 40%!important;\n  }\n}\n\n@media (max-width: 480px) {\n  \n  .navbar-fixed-top,\n  .navbar-fixed-bottom {\n    margin-right:0;\n    margin-left: 0;\n    margin-top: 0;\n  }\n  .sidebar-inside-sb input.inputbox {\n    width: 70% !important;\n  }\n\n  .go-top,dd.redirect,dd.lastpost,dd.posts,.panel-icon,dd.views {\n    display: none!important;\n  }\n  ul.topiclist dt {\n    width: