olours and backgrounds for common.css\n-------------------------------------------------------------- */\n\nhtml, body {\n	color: #333333;\n	background-color: #E2E1DF;	\n}\n\nh1 {\n	color: #FFFFFF;\n}\n\nh2 {\n	color: #28313F;\n}\n\nh3 {\n	border-bottom-color: #CCCCCC;\n	color: #555;\n}\n\nhr {\n	border-top: 1px solid #DDDDDD;\n  	border-bottom: 1px solid #ffffff;\n}\n\nhr.dashed {\n	border-top: 1px solid #DDDDDD;\n}\n\n/* Search box\n--------------------------------------------- */\n\n#search-box {\n	color: #FFFFFF;\n}\n\n#search-box #keywords {\n	background-color: #FFF;\n}\n\n#search-box input {\n	border: 1px solid #b0b0b0;\n}\n\n/* boxes and backgrounds\n---------------------------------------- */\n.header {\n  background: #222222;\n} \n\n.headerbar {\n	background-color: #12A3EB;	\n	color: #FFFFFF;\n}\n\n.panel {\n	background-color: #F2F2F2;\n	color: #28313F;\n}\n\n.post:target .content {\n	color: #000000;\n}\n\n.post:target h3 a {\n	color: #000000;\n}\n\n.bg1,.bg2,.bg3	{ background-color: #F2F2F2; }\n	\n.ucprowbg {\n	background-color: #DCDEE2;\n}\n\n.fieldsbg {\n	background-color: #eaeaea;\n}\n\n\n/* Horizontal lists\n----------------------------------------*/\n\nul.navlinks {\n	border-bottom-color: #FFFFFF;\n}\n\n/* Table styles\n----------------------------------------*/\ntable.table1 thead th {\n	color: #FFFFFF;\n	background-color: #222222;\n}\n\ntable.table1 tbody tr:hover, table.table1 tbody tr.hover {\n	background-color: #E9E9E9;	\n}\n\ntable.table1 tbody td {\n	border-bottom: 1px solid #DDDDDD;\n    border-top: 1px solid #FFFFFF;\n}\n\ntable.table1 tbody th {\n	border-bottom-color: #000000;\n	color: #333333;\n	background-color: #FFFFFF;\n}\n\n\n/* Misc layout styles\n---------------------------------------- */\ndl.details dt {\n	color: #111111;\n}\n\ndl.details dd {\n	color: #666666;\n}\n\n.sep {\n	color: #1198D9;\n}\n\n/* Pagination\n---------------------------------------- */\n\n.pagination span strong {\n	color: #FFFFFF;\n	background-color: #444444;\n	border-color: #444444;\n}\n\n.pagination span a, .pagination span a:link, .pagination span a:visited {\n	color: #555;\n	background-color: #ECEDEE;\n	border-color: #B4BAC0;\n}\n\n.pagination span a:hover {\n	border-color: #444444;\n	background-color: #444444;\n	color: #FFF;\n}\n\n.pagination span a:active {\n	color: #5C758C;\n	background-color: #ECEDEE;\n	border-color: #B4BAC0;\n}\n\n/* Pagination in viewforum for multipage topics */\n\n.row .pagination span a, li.pagination span a {\n	background-color: #FFFFFF;\n}\n\n.row .pagination span a:hover, li.pagination span a:hover {\n	background-color: #444444;\n}\n\n/* Miscellaneous styles\n---------------------------------------- */\n\n.copyright {\n	color: #555555;\n}\n\n.error {\n	color: #BC2A4D;\n}\n\n.reported {\n	background-color: #F7ECEF;\n}\n\nli.reported:hover {\n	background-color: #ECD5D8 !important;\n}\n.sticky, .announce {\n	/* you can add a background for stickies and announcements*/\n}\n\ndiv.rules {\n	background-color: #ECD5D8;\n	color: #BC2A4D;\n}\n\np.rules {\n	background-color: #ECD5D8;\n	background-image: none;\n}\n\n/*  	\n--------------------------------------------------------------\nColours and backgrounds for links.css\n-------------------------------------------------------------- */\n\n/* Links on gradient backgrounds */\n#search-box a:link, .navbg a:link, .forumbg .header a:link, .forabg .header a:link, th a:link {\n	color: #FFFFFF;\n}\n\n#search-box a:visited, .navbg a:visited, .forumbg .header a:visited, .forabg .header a:visited, th a:visited {\n	color: #FFFFFF;\n}\n\n#search-box a:hover, .navbg a:hover, .forumbg .header a:hover, .forabg .header a:hover, th a:hover {\n	color: #FFFFFF;\n}\n\n#search-box a:active, .navbg a:active, .forumbg .header a:active, .forabg .header a:active, th a:active {\n	color: #C8E6FF;\n}\n\n/* Links for forum/topic lists */\na.forumtitle {\n	color: #444444;\n}\n\n/* a.forumtitle:visited { color: #105289; } */\n\na.topictitle {\n	color: #444444;\n}\n\n/* a.topictitle:visited { color: #368AD2; } */\n\n\n/* Post body links */\n.postlink {\n	color: #D43A3A;\n	border-bottom-color: #368AD2;\n}\n\n.postlink:visited {\n	color: #D43A3A;\n	border-bottom-color: #5D8FBD;\n}\n\n.postlink:active {\n	color: #D43A3A;\n}\n\n.postlink:hover {\n	background-color: #D0E4F6;\n	color: #0D4473;\n}\n\n.signature a, .signature a:visited, .signature a:hover, .signature a:active {\n	background-color: transparent;\n}\n\n/* Profile links */\n.postprofile a:link, .postprofile a:visited, .postprofile dt.author a {\n	color: #AAAAAA;\n}\n\n.postprofile a:hover, .postprofile dt.author a:hover {\n	color: #111;\n}\n\n.postprofile a:active {\n	color: #105289;\n}\n\n/* Profile searchresults */	\n.search .postprofile a {\n	color: #444444;\n}\n\n.search .postprofile a:hover {\n	color: #444444;\n}\n\n/* Back to top of page */\n\na.top,a.up,a.down,a.left,a.right,a.top2 {\n	display: none;\n}\n\n/*  	\n--------------------------------------------------------------\nColours and backgrounds for content.css\n-------------------------------------------------------------- */\n\nul.forums {\n	background-color: #F2F2F2;	\n}\n\nul.topiclist li {\n	color: #666;\n}\n\n\nul.topiclist li.row dt a.subforum.read {\n	background-image: url("{IMG_SUBFORUM_READ_SRC}");\n}\n\nul.topiclist li.row dt a.subforum.unread {\n	background-image: url("{IMG_SUBFORUM_UNREAD_SRC}");\n}\n\nli.row {\n	border-top: 1px solid #FFFFFF;\n	border-bottom: 1px solid #DDD;\n}\n\nli.row strong {\n	color: inherit;\n}\n\nli.row:hover {\n	background-color: #E9E9E9;\n}\n\n\nli.header dt, li.header dd {\n	color: #FFFFFF;\n}\n\n/* Forum list column styles */\nul.topiclist dd.searchextra {\n	color: #333333;\n}\n\n/* Post body styles\n----------------------------------------*/\n.postbody {\n	color: #333333;\n}\n.postbody h3 a {\n	color: #444444;\n}\n.postbody ul.profile-icons li a {\n	color: #BBBBBB;\n}\n.postbody ul.profile-icons li a:hover {\n	color: #111;\n}\n\nli.warn-icon a:hover {\n	color: #C44664!important;\n}\n.ic-reported,.ic-unaproved {   \n  color: #C44664!important;\n}\n \n.icon-picture {   \n  color: #888888!important;\n}\n/* Content container styles\n----------------------------------------*/\n.content {\n	color: #555555;\n}\n\n.content h2, .panel h2 {\n	color: #222222;\n	border-bottom: 4px solid #444444;\n}\n\ndl.faq dt {\n	color: #444444;\n}\n\n.posthilit {\n	background-color: #F3BFCC;\n	color: #BC2A4D;\n}\n\n/* Post signature */\n.signature {\n	border-top-color: #CCCCCC;\n}\n\n/* Post noticies */\n.notice {\n	border-top-color:  #CCCCCC;\n}\n\n/* BB Code styles\n----------------------------------------*/\n/* Quote block */\nblockquote {\n	background-color: #EBEADD;\n	background-image: url("styles/Flato - Black and White/theme/images/quote.gif");\n	border-color:#DBDBCE;\n}\n\n.rtl blockquote {\n	background-image: url("styles/Flato - Black and White/theme/images/quote_rtl.gif");\n}\n\nblockquote blockquote {\n	/* Nested quotes */\n	background-color:#EFEED9;\n}\n\nblockquote blockquote blockquote {\n	/* Nested quotes */\n	background-color: #EBEADD;\n}\n\n/* Code block */\ndl.codebox {\n	background-color: #FFFFFF;\n	border-color: #C9D2D8;\n}\n\ndl.codebox dt {\n	border-bottom-color:  #CCCCCC;\n}\n\ndl.codebox code {\n	color: #2E8B57;\n}\n\n.syntaxbg		{ color: #FFFFFF; }\n.syntaxcomment	{ color: #FF8000; }\n.syntaxdefault	{ color: #0000BB; }\n.syntaxhtml		{ color: #000000; }\n.syntaxkeyword	{ color: #007700; }\n.syntaxstring	{ color: #DD0000; }\n\n/* Attachments\n----------------------------------------*/\n.attachbox {\n	background-color: #FFFFFF;\n	border-color:  #C9D2D8;\n}\n\n.pm-message .attachbox {\n	background-color: #F2F3F3;\n}\n\n.attachbox dd {\n	border-top-color: #C9D2D8;\n}\n\n.attachbox p {\n	color: #666666;\n}\n\n.attachbox p.stats {\n	color: #666666;\n}\n\n.attach-image img {\n	border-color: #999999;\n}\n\n/* Inline image thumbnails */\n\ndl.file dd {\n	color: #666666;\n}\n\ndl.thumbnail img {\n	border-color: #666666;\n	background-color: #FFFFFF;\n}\n\ndl.thumbnail dd {\n	color: #666666;\n}\n\ndl.thumbnail dt a:hover {\n	background-color: #EEEEEE;\n}\n\ndl.thumbnail dt a:hover img {\n	border-color: #368AD2;\n}\n\n/* Post poll styles\n----------------------------------------*/\n\nfieldset.polls dl {\n	border-top-color: #DCDEE2;\n	color: #666666;\n}\n\nfieldset.polls dl.voted {\n	color: #000000;\n}\n\nfieldset.polls dd div {\n	color: #FFFFFF;\n}\n\n.rtl .pollbar1, .rtl .pollbar2, .rtl .pollbar3, .rtl .pollbar4, .rtl .pollbar5 {\n	border-right-color: transparent;\n}\n\n.pollbar1 {\n	background-color: #AA2346;\n	border-bottom-color: #74162C;\n	border-right-color: #74162C;\n}\n\n.rtl .pollbar1 {\n	border-left-color: #74162C;\n}\n\n.pollbar2 {\n	background-color: #BE1E4A;\n	border-bottom-color: #8C1C38;\n	border-right-color: #8C1C38;\n}\n\n.rtl .pollbar2 {\n	border-left-color: #8C1C38;\n}\n\n.pollbar3 {\n	background-color: #D11A4E;\n	border-bottom-color: #AA2346;\n	border-right-color: #AA2346;\n}\n\n.rtl .pollbar3 {\n	border-left-color: #AA2346;\n}\n\n.pollbar4 {\n	background-color: #E41653;\n	border-bottom-color: #BE1E4A;\n	border-right-color: #BE1E4A;\n}\n\n.rtl .pollbar4 {\n	border-left-color: #BE1E4A;\n}\n\n.pollbar5 {\n	background-color: #F81157;\n	border-bottom-color: #D11A4E;\n	border-right-color: #D11A4E;\n}\n\n.rtl .pollbar5 {\n	border-left-color: #D11A4E;\n}\n\n/* Poster profile block\n----------------------------------------*/\n.postprofile {\n	color: #666666;	\n}\n.postprofile dt a img {   \n   background: #FAFAFA;\n   border: 1px solid #CCC;   \n}\n.postprofile dt,.postprofile dd {   \n   background: #EEEEEE;   \n} \n.rtl .postprofile {\n	border-right-color: #FFFFFF;\n	border-left-color: transparent;\n}\n\n\n.rtl .pm .postprofile {\n	border-right-color: #DDDDDD;\n	border-left-color: transparent;\n}\n\n.postprofile strong {\n	color: #444444;\n}\n\n.online {\n	color: #2ECC71;\n}\n.offline {\n	color: #D5D5D5;\n}\n\n\n/* Main CP box\n----------------------------------------*/\n\nul.cplist {\n	border-top-color: #B5C1CB;\n}\n\n#cp-main .pm-message {\n	border-color: #DBDEE2;\n	background-color: #FFFFFF;\n}\n\n/* CP tabbed menu\n----------------------------------------*/\n#tabs a {\n	\n}\n\n#tabs a span {	\n	color: #999;\n}\n\n#tabs a:hover span {\n	color: #111;\n}\n\n#tabs .activetab a {\n	\n}\n\n#tabs .activetab a span {\n	color: #111;\n}\n\n#tabs .activetab a:hover span {\n	color: #111;\n}\n\n/* Mini tabbed menu used in MCP\n----------------------------------------*/\n#minitabs li {\n	background-color: #E1EBF2;\n}\n\n#minitabs li.activetab {\n	background-color: #F9F9F9;\n}\n\n#minitabs li.activetab a, #minitabs li.activetab a:hover {\n	color: #333333;\n}\n\n/* UCP navigation menu\n----------------------------------------*/\n\n/* Link styles for the sub-section links */\n#navigation a {\n	background-color: #FAFAFA;\n    border: 1px solid #CCCCCC;    \n	color: #999;\n}\n\n#navigation a:hover {	\n	color: #111;\n}\n\n#navigation #active-subsection a {\n	color: #111;	\n}\n\n\n/* Preferences pane layout\n----------------------------------------*/\n#cp-main h2 {\n	color: #666;\n}\n#cp-main .panel .header dt a,#cp-main .panel .header dd a{    \n   color: #fff;   \n}\n#cp-main .pm {\n	background-color: #E8E8E8;\n}\n#cp-main .panel li.row {\n	border-bottom: 1px solid #dddddd;\n	border-top:  1px solid #ffffff;\n}\n/* Friends list */\n.cp-mini {\n	background-color: #eeeeee;\n}\n\ndl.mini dt {\n	color: #425067;\n}\n\n/* PM Styles\n----------------------------------------*/\n/* PM Message history */\n.current {\n	color: #000000 !important;\n}\n\n/* PM marking colours */\n.pmlist li.pm_message_reported_colour, .pm_message_reported_colour {\n	border-left-color: #BC2A4D;\n	border-right-color: #BC2A4D;\n}\n\n.pmlist li.pm_marked_colour, .pm_marked_colour {\n	border-color: #FF6600;\n}\n\n.pmlist li.pm_replied_colour, .pm_replied_colour {\n	border-color: #A9B8C2;\n}\n\n.pmlist li.pm_friend_colour, .pm_friend_colour {\n	border-color: #5D8FBD;\n}\n\n.pmlist li.pm_foe_colour, .pm_foe_colour {\n	border-color: #000000;\n}\n\n/* Avatar gallery */\n#gallery label {\n	background-color: #FFFFFF;\n	border-color: #CCC;\n}\n\n#gallery label:hover {\n	background-color: #EEE;\n}\n\n/*  	\n--------------------------------------------------------------\nColours and backgrounds for forms.css\n-------------------------------------------------------------- */\n\n/* General form styles\n----------------------------------------*/\nselect {\n	border: 1px solid #CCCCCC;\n	background-color: #FAFAFA;\n	color: #777;\n}\n\nlabel {\n	color: #666666;\n}\n\noption.disabled-option {\n	color: graytext;\n}\ninput[type="checkbox"] {\n	background-color: transparent!important;\n}\n/* Definition list layout for forms\n---------------------------------------- */\ndd label {\n	color: #333;\n}\n\n/* Hover effects */\nfieldset dl:hover dt label {\n	color: #000000;\n}\n\nfieldset.fields2 dl:hover dt label {\n	color: inherit;\n}\n\n/* Quick-login on index page */\nfieldset.quick-login input.inputbox {\n	background-color: #F2F3F3;\n}\n\n/* Posting page styles\n----------------------------------------*/\n\n#message-box textarea {\n	color: #333333;\n}\n\n/* Input field styles\n---------------------------------------- */\n.inputbox {\n	background-color: #FFFFFF;\n	border: 1px solid #CCCCCC;	\n	color: #777;\n}\n.inputbox:focus {		\n	color: #333;\n}\n\n/* Form button styles\n---------------------------------------- */\n\n/* <a> button in the style of the form buttons */\na.button1,a.button2,\ninput.button1,input.button2,input.button3,.buttons a{\n	color: #777777;\n	border: 1px solid #CCCCCC;\n	background-color: #FAFAFA;\n}\n\n/* Hover states */\na.button1:hover,a.button2:hover,.buttons a:hover,\ninput.button1:hover,input.button2:hover,input.button3:hover {\n	color: #111;	\n	background-color: #FFF;\n}\n\ninput.disabled {\n	color: #666666;\n}\n/*******************************************************************************!\nHide or show the Right-Content Forum Menu or the Sidebar Forum Menu blocks \n********************************************************************************/\n\n#forum-menu {\n  display: none; /* Change display: none;  to  display: block;  to show the Right-Content Forum Menu */\n}\n\n#sidebar #forum-menu {\n  display: block; /* Change display: block;  to  display: none;  to hide the Sidebar Forum Menu */\n}\n\n/*******************************************************************************!\nChange the side of the postprofile and postbody \n********************************************************************************/\n\n/* Uncomment this to move the postprofile to the right side\n\n.postprofile {    \n    float: right!important;    \n}\n.postbody {    \n    float: left!important;    \n}\n.profile-up-right {\n    float: none;   \n}\n\n*/\n\n/**********************************************!\n Header Styles\n ***********************************************/\n\n#page-header,#page-body,#page-copyrights,#page-footer {\n  width:1170px;\n  margin-left: auto;\n  margin-right: auto; \n  position: relative;   \n}\n#page-body:before,#page-header:before, #page-copyrights:before,#page-footer:before,\n#page-body:after,#page-header:after,#page-copyrights:after,#page-footer:after {\n  content: "";\n  display: table;\n  line-height: 0;\n}\n#page-body:after,#page-header:after,#page-copyrights:after,#page-footer:after {\n  clear: both;\n}\n\n#wrapheader {  \n  padding: 0; \n  height: 100px;  \n  overflow: hidden;\n  clear: both;\n  width: 100%; \n  z-index: 100; \n}\n.holdericon {\n  display: block;    \n  height: 45px;\n  position: relative;    \n  margin-top: 26px;\n  text-align: center;    \n  font-size: 2.769em!important; \n}\n.navbar-links a i {\n  position: relative;\n  right: 9px;    \n  margin-top: 0px;\n  vertical-align: top;\n}\n.icon-right-circled:before {\n  position: relative;\n  right: 2px!important;   \n} \n.header-label {\n  position: relative;\n  display: block;\n  left: 10px; \n}\n.logodiv {    \n  width: 250px;\n  z-index: 1031;\n  max-height: 100px;\n  height: 100px;     \n  position: relative;\n  text-align: center;\n} \n.logodiv img{\n  position:relative;    \n  top: 0px;\n  z-index: 6666;\n  max-height:100%;\n}        \n.logodiv a {   \n  position: relative;  \n  width: 100%;\n  height:100px;\n  display: block; \n  text-decoration: none;\n  font-size: 3em;\n} \n.logodiv h1{ \n  \n  font-family: ''Anton'', sans-serif;\n  position: relative;\n  top: 0;\n  text-transform: lowercase;\n  line-height: 100px;\n  vertical-align: middle;\n  width: 100%; \n}\n.navbar-links {\n  list-style: none outside none;   \n}\n.navbar-links > li {\n  float: left;     \n  width: 100px;\n  height: 100px;\n  display: block;\n}\n.navbar-links > li > a {\n  padding:0;\n  line-height: 1.538em;\n  width: 100px;\n  height: 100px;\n  overflow: hidden;   \n  position: relative;\n  display: block;  \n  text-transform: capitalize;\n  text-decoration: none;\n  font-size: 1em;   \n}\nli#register { \n  border-right: none;\n}\nli#register { \n  position: relative;\n} \n.msg-counter,.msg-counter-bg {   \n  position: absolute;\n  width: 26px;\n  height: 23px;\n  left: 10px;\n  overflow: hidden;\n  top: 4px;\n  z-index: 6;    \n}  \n.msg-counter strong,.msg-counter-bg strong{\n  margin-right: 10px;\n  padding: 2px 5px;\n  position: relative;\n  font-size: 12px;\n  top: 2px;    \n  -webkit-border-radius: 15px;\n  -moz-border-radius: 15px;\n  border-radius: 15px;    \n} \n\n /************************************************ \n Sizes for the smaller menu \n ***********************************************/\n\n#wrapheader.small { height: 50px!important; }\n#wrapheader.small .logodiv { height: 50px!important; }\n#wrapheader.small .logodiv h1 a { height: 50px!important;line-height: 50px;font-size: 2.462em; }\n#wrapheader.small .navbar-links li { height: 50px!important;width: 50px !important; }\n#wrapheader.small .navbar-links li a { height: 50px !important;width: 50px !important;}\n#wrapheader.small .navbar-links a i { position: relative;margin-t